import 'package:flutter/material.dart';import 'package:hotslogs_mobile_client/talent_builds.dart';import 'package:cached_network_image/cached_network_image.dart';class HeroDetailsState extends State<HeroDetails> {  List<TalentBuild> _talentBuilds;  @override  void initState() {    _loadTalentBuild(widget.heroName);  }  void _loadTalentBuild(String heroName) {    setState(() async {      _talentBuilds = await getBuildsForHero(heroName);    });  }  @override  Widget build(BuildContext context) {    if (_talentBuilds == null) {      return CircularProgressIndicator();    }    return Scaffold(      appBar: AppBar(        title: Text("Talent Builds - ${widget.heroName}"),      ),      body: ListView(        children: _talentBuilds.map(_buildListItem).toList(),      ),    );  }  Widget _buildListItem(TalentBuild talentBuild) => Padding(        padding: EdgeInsets.symmetric(vertical: 8.0),        child: Row(          mainAxisAlignment: MainAxisAlignment.spaceEvenly,          crossAxisAlignment: CrossAxisAlignment.start,          children: talentBuild              .mapTalents((talent) => _buildTalentCell(talent))              .toList(),        ),      );  Widget _buildTalentCell(Talent talent) => Expanded(        child: Padding(          padding: EdgeInsets.symmetric(horizontal: 8.0),          child: Column(            children: <Widget>[              CachedNetworkImage(                imageUrl: talent.imageUrl,              ),              Padding(                padding: EdgeInsets.only(top: 4.0),                child: Text(                  talent.name,                  style: TextStyle(fontSize: 8.0),                  textAlign: TextAlign.center,                ),              ),            ],          ),        ),      );}class HeroDetails extends StatefulWidget {  final String heroName;  const HeroDetails(this.heroName, {Key key}) : super(key: key);  @override  State<HeroDetails> createState() {    return HeroDetailsState();  }}